    String user,user_name;
    public static int user_sesion;
    
    public Administrator() {
        
        
        initComponents();
        user=Logo.user;
        user_sesion=1;
        
        addWindowListener(new WindowAdapter() {
        @Override
        public void windowClosing(WindowEvent e) {
            dispose();
        }
        });
        
        setTitle("Administrator - Sesion of  " + user);
        setSize(650,430);
        
        setResizable(true);
        this.setLocationRelativeTo(null);
        
        //setDefaultCloseOperation(WindowConstants.EXIT_ON_CLOSE);
        
        ImageIcon wallpaper= new ImageIcon("src/images/wallpaperPrincipal.jpg");
        Icon ic=new ImageIcon(wallpaper.getImage().getScaledInstance(jl_wallpapper.getWidth(), jl_wallpapper.getHeight(),Image.SCALE_DEFAULT));
        jl_wallpapper.setIcon(ic);
        this.repaint();
        
        try{
            
            Connection cn=DriverManager.getConnection("jdbc:mysql://localhost/db_project","root","");
            PreparedStatement ps=cn.prepareStatement("select nombre_usuario from usuarios where username='" + user +"'");
            ResultSet rs=ps.executeQuery();
            
            if(rs.next()){
                user_name=rs.getString("nombre_usuario");
                jl_username.setText(user_name);
                
            }
            
        }
        
        catch(Exception e){
            System.out.println("Not data found please enter a valid user");
        }
    }
    
    
