    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        int count=0;
        String vClassDev="";
        String vMark="";
        String vStatus="";
        
        if((jc_cdev.getSelectedIndex()+1)==1){
            vClassDev="Laptop";
        }else if((jc_cdev.getSelectedIndex()+1)==2){
            vClassDev="Desktop";
        }else if((jc_cdev.getSelectedIndex()+1)==3){
            vClassDev="Printer";
        }else if((jc_cdev.getSelectedIndex()+1)==4){
            vClassDev="MultiFunctional";
        }
        
        
        if((jc_mark.getSelectedIndex()+1)==1){
            vMark="Acer";
        }else if((jc_mark.getSelectedIndex()+1)==2){
            vMark="Allenware";
        }else if((jc_mark.getSelectedIndex()+1)==3){
            vMark="Apple";
        }else if((jc_mark.getSelectedIndex()+1)==4){
            vMark="Asus";
        }else if((jc_mark.getSelectedIndex()+1)==5){
            vMark="Brother";
        }else if((jc_mark.getSelectedIndex()+1)==6){
            vMark="Dell";
        }else if((jc_mark.getSelectedIndex()+1)==7){
            vMark="HP";
        }else if((jc_mark.getSelectedIndex()+1)==8){
            vMark="Lenovo";
        }
        
        if((jc_status.getSelectedIndex()+1)==1){
            vStatus="New Entry";
        }else if((jc_status.getSelectedIndex()+1)==2){
            vStatus="Not Fixed";
        }else if((jc_status.getSelectedIndex()+1)==3){
            vStatus="Checking out";
        }else if((jc_status.getSelectedIndex()+1)==4){
            vStatus="Sort out";
        }else if((jc_status.getSelectedIndex()+1)==5){
            vStatus="Shipped out";
        }
        
        if((jt_cn.getText().trim()).equals("")){
            jt_cn.setBackground(Color.red);
            count++;
        }
        if((jt_model.getText().trim()).equals("")){
            jt_model.setBackground(Color.red);
            count++;
        }
        if((jt_serie.getText().trim()).equals("")){
            jt_serie.setBackground(Color.red);
            count++;
        }
        if((jt_la.getText().trim()).equals("")){
            jt_la.setBackground(Color.red);
            count++;
        }
        if((jt_de.getText().trim()).equals("")){
            jt_de.setBackground(Color.red);
            count++;
        }
        if((jt_obs.getText().trim()).equals("")){
            jt_obs.setBackground(Color.red);
            count++;
        }
        if((jt_comments.getText().trim()).equals("")){
            jt_comments.setBackground(Color.red);
            count++;
        }
        
        if(count==0){
        try{
            Connection cn=Connect.Cnnct();
            PreparedStatement ps=cn.prepareStatement("update equipos set tipo_equipo=?,marca=?,modelo=?,num_serie=?,ultima_modificacion=?,estatus=?,observaciones=?,comentarios_tecnicos=? where id_equipo='"+id_device+"'");
            ps.setString(1, vClassDev);
            ps.setString(2, vMark);
            ps.setString(3, jt_model.getText().trim());
            ps.setString(4, jt_serie.getText().trim());
            ps.setString(5, jt_la.getText().trim());
            ps.setString(6, vStatus);
            ps.setString(7, jt_obs.getText().trim());
            ps.setString(8, jt_comments.getText().trim());
            ps.executeUpdate();
            JOptionPane.showMessageDialog(null,"Success Update!");
        }
        catch(Exception e){
            JOptionPane.showMessageDialog(null,"An error pop up to update the fields at database please try again");
        }
        }
        else{
            JOptionPane.showMessageDialog(null,"Please fill all the fields out!");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

